//colors
$primary-color: #F3737B;
$secondary-color: #80647D;
$white: #FFFFFF;
$black: #000000;


// nav
.nav-container {
    position: absolute;
    padding: 4rem;
    top: 0;
    left: 0;
    .nav-icon {
        background-color: $primary-color;
        position: absolute;
        right: 4rem;
        top: 2.5rem;
        z-index: 10;
        width: 6rem;
        height: 6rem;
        border-radius: 50%;
        cursor: pointer;
        position: fixed;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;


        .bar1, .bar2, .bar3 {
            width: 20px;
            height: 2px;
            margin: 4px 0;
            transition: 0.4s;
            background-color: $white;
            border-radius: 1rem;
        }
        .bar2 {
            width: 30px;
            transform-origin: right;
        }
    }

    .nav-main {
        position: fixed;
        top: 0;
        left: 0;
        z-index: 1;
        .box-wrapper {
            position: absolute;
            top: 0;
            left: 0;
            z-index: 1;
            display: flex;
            .box {
                background-color: $secondary-color;
                width: calc(100vw / 4);
                height: 0vh;
            }
        }
        .mobile-logo {
            position: absolute;
            top: 4rem;
            left: 4rem;
            z-index: 2;
            filter: brightness(1%);
            opacity: 0;
            font-size: 2.5rem;
        }
        &__content {
            opacity: 0;
            visibility: hidden;
            width: 95vw;
            position: absolute;
            top: 15rem;
            left: 4rem;
            z-index: 2;
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(35rem, 1fr));
            grid-gap: 1rem;
            padding: 4rem;
            color: $black;

            &--box {
                display: flex;
                flex-direction: column;
                justify-content: space-between;
                min-height: 25rem;
                .social-links {
                    display: flex;
                    flex-direction: column;
                    &--box {
                        display: flex;
                        align-items: center;
                        a {
                            font-size: 3rem;
                            margin-right: 1.6rem;
                            filter: brightness(1%);
                            &:hover{
                                font-size: 4rem;
                            }
                        }
                        
                    }
                    &--box:nth-child(2) {
                        margin-top: 1rem;
                    }
                }
                .resume {
                    font-size: 4rem;
                    text-decoration: none;
                    color: $black;
                    letter-spacing: -2px;
                    align-self: flex-start;
                    transition: all .2s ease-in-out;
                    font-family: 'Roboto Slab', serif;
                    font-weight: 500;
                        &:hover {
                            letter-spacing: 3px;
                        }
                }
                .list {
                    justify-self: flex-start;
                    margin-top: 1rem;
                    &__item {
                        list-style: none;
                        &:not(:last-child) {
                            margin-bottom: 3.5rem;
                        }
                        a {
                            text-transform: uppercase;
                            font-size: 3rem;
                            text-decoration: none;
                            color: $black;
                            font-family: 'Roboto Slab', serif;
                            font-weight: 550;
                            letter-spacing: -1px;
                            transition: all .2s ease-in-out;
                            &:hover {
                                letter-spacing: 3px;
                            }
                        }
                    }
                }
            }
        }
    }

    .nav-sub {
        position: absolute;
    }
}

.header {
    position: relative;
    overflow: hidden;
    min-height: 50rem;  
    @media only screen and (max-width: 37.5em) { // phone
        min-height: 70rem;    
    }
    &:after {
        content: "";
        position: absolute;
        width: 100vw;
        height: 100%;
