        }
    }
    .project-box:nth-child(1) {
        background: linear-gradient(to bottom, #EFB7D7, #f5f5f5);
    }
    .project-box:nth-child(2) {
        background: linear-gradient(to bottom, #B1E5F9, #F4D2FE);
    }
    .project-box:nth-child(3) {
        background: linear-gradient(to bottom, #DBB4F3, #EFB7D7);
    }
    .project-box:nth-child(4) {
        background: linear-gradient(to bottom, #EFA971, #E4CAFE);

    }
    .project-box:nth-child(5) {
        background: linear-gradient(to right, #B1E5F9, #F4D2FE);

    }
    .project-box:nth-child(6) {
        background: linear-gradient(to right, #f0b1f9, #aeeeee);

    }

    &-box__link {
        align-self: flex-end;
        width: 30%;
        display: flex;
        justify-content: space-between;
        align-items: center;
        a{
            color: $black;
        }
        @media only screen and (max-width: 56.25em) { // tab-port
          //  width: 30%;
        }

    }
    &-box__content {
        align-self: flex-start
    }
}

// hover state
.project-box:nth-child(1), .project-box:nth-child(2), .project-box:nth-child(3), .project-box:nth-child(4), .project-box:nth-child(5), .project-box:nth-child(6) {
    background: linear-gradient(to bottom, #F3CBAB, #FEEDCA);
    &:after {
        content: '';
        position: absolute;
        width: 103%;
        height: 101%;
        top: 1rem;
        right: 0;
        z-index: -1;
    }
    &:hover {
        background: none;
        background-color: $secondary-color
    }
    &:hover  img {
        filter: invert(1);
    }
    &:hover:after {
        outline: 2px solid $secondary-color;
    }
}

